  // 64 bit CPU, write 8 bytes at a time
  for (int j = 0; j < N; j += 8) {
    uint64_t data = 0;
    for (int k = 0; k < 8; k++) {
      if (j + k < N)
        data += ((uint64_t)B_mat[j + k][0] & 0xFF) << (k * 8);
    }
    write_data_64(addr, data);
    addr += 8;
  }

